    private void changeText(javax.swing.JLabel label, String newText) {
        label.setText(newText);
    }
    
    private void valorInicioStateChanged(javax.swing.event.ChangeEvent evt) {//GEN-FIRST:event_valorInicioStateChanged
        changeText(lblInicio, Integer.toString(valorInicio.getValue()));
    }//GEN-LAST:event_valorInicioStateChanged

    private void valorFimStateChanged(javax.swing.event.ChangeEvent evt) {//GEN-FIRST:event_valorFimStateChanged
        changeText(lblFim, Integer.toString(valorFim.getValue()));
    }//GEN-LAST:event_valorFimStateChanged

    private void valorPassoStateChanged(javax.swing.event.ChangeEvent evt) {//GEN-FIRST:event_valorPassoStateChanged
        changeText(lblPasso, Integer.toString(valorPasso.getValue()));
    }//GEN-LAST:event_valorPassoStateChanged

    private void btnContarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnContarActionPerformed
       int inicio, passo, fim;
       inicio = valorInicio.getValue();
       passo = valorPasso.getValue();
       fim = valorFim.getValue();
       
       String finalText = "";
       
       for (int i = inicio; i <= fim; i+=passo) {
           finalText += String.format("%s \n", i);
       }
       
       finalText += "";
       
       lblRes.setText(finalText);
    }//GEN-LAST:event_btnContarActionPerformed

